/**
 * The hacToDec methods return hexadecimal to decimal numbers.
 * @param {String} hex Hex value for conversion.
 * @returns {Number} It returned a hex equivalent decimal value.
 */
export function hexToDec(hex: string): number;


 /**
  * The hacToOct methods return hexadecimal to Octal numbers.
  * @param {String} hex Hex value for conversion.
  * @returns {String} It returned a hex equivalent ocatal value.
  */
export function hexToOct(hex: string): string;


 /**
  * The hacToBin methods return hexadecimal to binary numbers.
  * @param {String} hex Hex value for conversion.
  * @returns {String} It returned a hex equivalent binary value.
  */
export function hexToBin(hex: string): string;


 /**
  * The decToHex methods return decimal to hexadecimal numbers.
  * @param {Number} dec Dec value for conversion.
  * @returns {String} It returned a dec equivalent hexadecimal value.
  */
export function decToHex(dec: number): string;


 /**
  * The decToOct methods return decimal to Octal numbers.
  * @param {Number} dec Dec value for conversion.
  * @returns {String} It returned a dec equivalent octal value.
  */
export function decToOct(dec: number): string;


 /**
  * The decToBin methods return decimal to binary numbers.
  * @param {Number} dec Dec value for conversion.
  * @returns {String} It returned a dec equivalent binary value.
  */
export function decToBin(dec: number): string;


 /**
  * The octToHex methods return octal to hexadecimal numbers.
  * @param {String} oct Oct value for conversion.
  * @returns {String} It returned a oct equivalent hexadecimal value.
  */
export function octToHex(oct: string): string;


 /**
  * The octToBin methods return octal to decimal numbers.
  * @param {String} oct Oct value for conversion.
  * @returns {Number} It returned a oct equivalent decimal value.
  */
export function octToDec(oct: string): number;


 /**
  * The octToBin methods return octal to binary numbers.
  * @param {String} oct Oct value for conversion.
  * @returns {String} It returned a oct equivalent binary value.
  */
export function octToBin(oct: string): string;


 /**
  * The binToHex methods return binary to hexadecimal numbers.
  * @param {String} bin Binary value for conversion.
  * @returns {String} It returned a binary equivalent hexadecimal value.
  */
export function binToHex(bin: string): string;


 /**
  * The binToDec methods return binary to decimal numbers.
  * @param {String} bin Binary value for conversion.
  * @returns {Number} It returned a binary equivalent decimal value.
  */
export function binToDec(bin: string): number;


 /**
  * The binToOct methods return binary to octal numbers.
  * @param {String} bin Binary value for conversion.
  * @returns {String} It returned a binary equivalent octal value.
  */
export function binToOct(bin: string): string;
